import React, {useState, useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import {SearchBarForResult} from './SearchBar';
import { Card } from './Card';
import { Link } from 'react-router-dom';

const Result = (props) => {
    const location = useLocation();
    const [filteredData, setFilteredData] = useState([]);
    const { concertData } = props;
    const [cityName, setCityName] = useState('');

    useEffect(() => {
        const searchParams = location.state ? location.state.searchParams : { cityZipCode: '', artistEventVenue: '' };

        if (concertData) {
            const filtered = concertData.filter((event) => {
                const locationMatch = searchParams.cityZipCode ? event.location_city_state.toLowerCase().includes(searchParams.cityZipCode.toLowerCase()) : true;
                const titleMatch = searchParams.artistEventVenue ? event.event_title.toLowerCase().includes(searchParams.artistEventVenue.toLowerCase()) : true;
                return locationMatch && titleMatch;
            });
            setFilteredData(filtered);

            if ((searchParams.cityZipCode || searchParams.artistEventVenue) && filtered.length > 0) {
                setCityName(filtered[0].location_city_state);
            } else {
                setCityName('');
            }
        }
    }, [concertData, location.state]);

    const eventElements = filteredData.map((event, index) => (
        <div key={index} className="event-info">
            <p className="date">{event.date}</p>
            <p className="details">{event.event_title} - {event.location_name}</p>
            <Link to={`/Infopage/${encodeURIComponent(event.event_title)}`}>
                <button className="info-btn">More info</button>
            </Link>
        </div>
    ));

    return (
        <div>
            <header>
                <SearchBarForResult />
            </header>
            <main>
                <section className="feature-1">
                    <h1>Discover New Favorites</h1>
                    <Card concertData={concertData}/>
                </section>

                <section className="Event">
                    <h1>Event In <span>{cityName}</span></h1>
                </section>

                <section>
                    {filteredData.length > 0 ? (
                        <div className="event-list">
                            {eventElements}
                        </div>
                    ) : (
                        <div className="event-info">
                            <p className="no-result">No Events In {cityName}</p>
                        </div>
                    )}
                </section>
            </main>
        </div>
    );
};

export default Result;
